                        <div className="container mx-auto px-6">
                            <motion.div
                                initial={{ opacity: 0 }}
                                whileInView={{ opacity: 1 }}
                                viewport={{ once: true, margin: "-100px" }}
                                transition={{ duration: 0.8 }}
                            >
                                <h2 className="text-3xl md:text-5xl font-medium mb-16">Récentes réalisations</h2>

