import { motion } from 'framer-motion';

export default function Portfolio() {
    const projets = [
        {
            titre: "Clando Ga",
            description: "Application mobile de location immobilière",
            categories: ["UI/UX", "Développement", "Branding"],
            image: "/projets/clando.jpg",
            lien: "/portfolio/clando"
        },
        {
            titre: "Lumen",
            description: "Système de design et application mobile",
            categories: ["Design System", "Frontend"],
            image: "/projets/lumen.jpg",
            lien: "/portfolio/lumen"
        },
        {
            titre: "Nova",
            description: "Identité visuelle complète",
            categories: ["Branding", "Print"],
            image: "/projets/nova.jpg",
            lien: "/portfolio/nova"
        }
    ];

    return (
        <div className="min-h-screen bg-[#faf9f5] text-[#1a1a1a] py-20">
            <div className="container mx-auto px-6">
                <motion.div
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.5 }}
                    className="mb-16"
                >
                    <h1 className="text-5xl md:text-7xl font-medium mb-4">Nos réalisations</h1>
                    <p className="text-xl max-w-2xl">
                        Découvrez nos projets les plus marquants et notre expertise en action.
                    </p>
                </motion.div>

                <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
                    {projets.map((projet, index) => (
                        <motion.div
                            key={index}
                            initial={{ opacity: 0, y: 20 }}
                            whileInView={{ opacity: 1, y: 0 }}
                            transition={{ duration: 0.5, delay: index * 0.1 }}
                            viewport={{ once: true }}
                            className="group"
                        >
                            <div className="relative overflow-hidden rounded-2xl aspect-square bg-gray-200">
                                <img
                                    src={projet.image}
                                    alt={projet.titre}
                                    className="w-full h-full object-cover transition-transform duration-500 group-hover:scale-110"
                                />
                                <div className="absolute inset-0 bg-gradient-to-t from-black/70 to-transparent opacity-0 group-hover:opacity-100 transition-opacity flex items-end p-6">
                                    <div>
                                        <h3 className="text-2xl text-white font-medium">{projet.titre}</h3>
                                        <p className="text-gray-300 mt-1">{projet.description}</p>
                                        <div className="flex flex-wrap gap-2 mt-3">
                                            {projet.categories.map((categorie, i) => (
                                                <span key={i} className="px-3 py-1 rounded-full bg-white/20 text-white text-xs">
                                                    {categorie}
                                                </span>
                                            ))}
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </motion.div>
                    ))}
                </div>
            </div>
        </div>
    );
}
