import { useState, useRef } from 'react';
import { motion, useScroll, useTransform } from 'framer-motion';
import { FaInstagram, FaLinkedin } from 'react-icons/fa'; // Import des icônes

// Configuration modifiable
const config = {
    // Personalisation
    logoLight: 'beyond_logo_light.svg',
    logoDark: 'beyond_logo_dark.svg',
    nomAgence: "Beyond",
    slogan: "Votre identité, notre vision.",

    // Réseaux sociaux
    reseaux: {
        instagram: "https://instagram.com/votrecompte",
        linkedin: "https://linkedin.com/company/votrecompte",
        dribbble: "https://dribbble.com/votrecompte"
    },

    // Contact
    email: "contact@beyond.studio",
    telephone: "+33 1 23 45 67 89",
    adresse: "Paris, France",

    // Services illustrés
    services: [
        {
            titre: "Identité visuelle",
            description: "Création d'une identité unique pour votre marque",
            illustration: "/illustrations/identite.svg",
            motsCles: ["Logo", "Charte graphique", "Direction artistique"]
        },
        {
            titre: "Branding",
            description: "Développement de votre stratégie de marque",
            illustration: "/illustrations/branding.svg",
            motsCles: ["Positionnement", "Stratégie", "Guidelines"]
        },
        {
            titre: "UI/UX Design",
            description: "Interfaces intuitives et expériences mémorables",
            illustration: "/illustrations/uiux.svg",
            motsCles: ["Prototypage", "Wireframes", "User testing"]
        },
        {
            titre: "Développement web",
            description: "Solutions techniques sur-mesure",
            illustration: "/illustrations/dev.svg",
            motsCles: ["React", "Performances", "Responsive"]
        }
    ],

    // Réalisations récentes
    realisations: [
        {
            titre: "Nova Collective",
            description: "Identité visuelle & Site web",
            categories: ["Branding", "UI/UX", "Développement"],
            vignette: "/projets/nova.jpg",
            lien: "/portfolio/nova"
        },
        {
            titre: "Lumen",
            description: "Application mobile & Design system",
            categories: ["UI/UX", "Design System", "Frontend"],
            vignette: "/projets/lumen.jpg",
            lien: "/portfolio/lumen"
        }
    ]
};

export default function Home() {
    const [darkMode, setDarkMode] = useState(false);
    const containerRef = useRef(null);

    const { scrollYProgress } = useScroll({
        target: containerRef,
        offset: ["start start", "end end"]
    });

    // Animations basées sur le scroll
    const opacity = useTransform(scrollYProgress, [0, 0.1], [1, 0]);
    const y = useTransform(scrollYProgress, [0, 0.1], [0, -100]);

    return (
        <div
            className={`relative ${darkMode ? 'bg-[#0a0a0a] text-gray-100' : 'bg-[#faf9f5] text-[#1a1a1a]'}`}
            ref={containerRef}
        >
            {/* Vidéo de fond pour l'héro */}
            <div className="fixed inset-0 z-0 overflow-hidden">
                <video
                    autoPlay
                    loop
                    muted
                    playsInline
                    className="w-full h-full object-cover opacity-20"
                >
                    <source src="/videos/background.mp4" type="video/mp4" />
                </video>
            </div>

            {/* Header sticky */}
            <motion.header
                style={{ opacity, y }}
                className={`fixed w-full z-50 ${darkMode ? 'bg-[#0a0a0a]/80' : 'bg-[#faf9f5]/80'} backdrop-blur-md border-b ${darkMode ? 'border-[#1f1f1f]' : 'border-[#eaeaea]'}`}
            >
                <div className="container mx-auto px-6 py-4 flex items-center justify-between">
                    <div className="w-32 h-22 md:w-22 md:h-22">
                        <img
                            src={darkMode ? config.logoLight : config.logoDark}
                            alt={config.nomAgence}
                            className="w-full h-full"
                        />
                    </div>

                    <nav className="hidden md:flex items-center gap-8">
                        <a href="#accueil" className="nav-link">Accueil</a>
                        <a href="#services" className="nav-link">Services</a>
                        <a href="#realisations" className="nav-link">Réalisations</a>
                        <a href="/portfolio" className="nav-link">Portfolio</a>
                        <a href="/contact" className="nav-link">Contact</a>
                    </nav>

                    <ThemeToggle darkMode={darkMode} setDarkMode={setDarkMode} />
                </div>
            </motion.header>

            {/* Contenu principal avec ancres */}
            <main className="relative z-10">
                {/* Section Accueil */}
                <section id="accueil" className="min-h-screen flex items-center relative bg-gradient-to-b from-transparent to-[#f0f0f0]">
                    <div className="container mx-auto px-6 py-32">
                        <motion.div
                            initial={{ opacity: 0, y: 40 }}
                            animate={{ opacity: 1, y: 0 }}
                            transition={{ duration: 1 }}
                            className="max-w-4xl"
                        >
                            <h1 className="text-5xl md:text-7xl font-medium mb-6 leading-tight">
                                {config.nomAgence} —<br />
                                <span className={darkMode ? 'text-gray-300' : 'text-gray-600'}>
                                    {config.slogan}
                                </span>
                            </h1>
                            <p className="text-xl md:text-2xl mb-10 max-w-2xl">
                                Nous transformons les idées en expériences digitales exceptionnelles.
                            </p>
                            <div className="flex gap-4">
                                <motion.a
                                    href="#services"
                                    whileHover={{ y: -2 }}
                                    whileTap={{ scale: 0.98 }}
                                    className={`px-8 py-3 rounded-full ${darkMode ? 'bg-white text-black' : 'bg-black text-white'}`}
                                >
                                    Nos services
                                </motion.a>
                                <motion.a
                                    href="#realisations"
                                    whileHover={{ y: -2 }}
                                    whileTap={{ scale: 0.98 }}
                                    className={`px-8 py-3 rounded-full border ${darkMode ? 'border-gray-600 hover:bg-gray-800' : 'border-gray-300 hover:bg-gray-100'}`}
                                >
                                    Nos réalisations
                                </motion.a>
                            </div>
                            {/* Icônes des réseaux sociaux */}
                            <div className="absolute bottom-10 right-10 flex gap-6">
                                <motion.a href={config.reseaux.instagram} target="_blank" rel="noopener noreferrer" whileHover={{ scale: 1.1 }}>
                                    <FaInstagram className={`text-4xl ${darkMode ? 'text-white' : 'text-black'}`} />
                                </motion.a>
                                <motion.a href={config.reseaux.linkedin} target="_blank" rel="noopener noreferrer" whileHover={{ scale: 1.1 }}>
                                    <FaLinkedin className={`text-4xl ${darkMode ? 'text-white' : 'text-black'}`} />
                                </motion.a>
                            </div>
                        </motion.div>
                    </div>
                </section>

                {/* Section Services */}
                <section id="services" className="py-20 bg-gradient-to-b from-[#e0f7fa] to-[#ffffff]">
                    <div className="container mx-auto px-6">
                        <motion.div
                            initial={{ opacity: 0 }}
                            whileInView={{ opacity: 1 }}
                            viewport={{ once: true, margin: "-100px" }}
                            transition={{ duration: 0.8 }}
                        >
                            <h2 className="text-3xl md:text-5xl font-medium mb-16">Nos expertises</h2>

                            <div className="grid md:grid-cols-2 gap-12">
                                {config.services.map((service, index) => (
                                    <ServiceCard
                                        key={index}
                                        service={service}
                                        darkMode={darkMode}
                                        index={index}
                                    />
                                ))}
                            </div>
                        </motion.div>
                    </div>
                </section>

                {/* Section Réalisations */}
                <section id="realisations" className="py-20 bg-gradient-to-b from-[#fafafa] to-[#f0f0f0]">
                    <div className="container mx-auto px-6">
                        <motion.div
                            initial={{ opacity: 0 }}
                            whileInView={{ opacity: 1 }}
                            viewport={{ once: true, margin: "-100px" }}
                            transition={{ duration: 0.8 }}
                        >
                            <h2 className="text-3xl md:text-5xl font-medium mb-16">Récentes réalisations</h2>

                            <div className="grid md:grid-cols-2 gap-12">
                                {config.realisations.map((realisation, index) => (
                                    <RealisationCard
                                        key={index}
                                        realisation={realisation}
                                    />
                                ))}
                            </div>
                        </motion.div>
                    </div>
                </section>

                {/* Footer */}
                <footer className={`py-12 ${darkMode ? 'bg-[#111]' : 'bg-[#f9f9f9]'}`}>
                    <div className="container mx-auto px-6 text-center">
                        <p className={`text-sm ${darkMode ? 'text-white' : 'text-black'}`}>© 2025 {config.nomAgence}. Tous droits réservés.</p>
                    </div>
                </footer>
            </main>
        </div>
    );
}
