import { useState } from "react";
import { motion } from "framer-motion";

function Button({ onClick, children, variant = "ghost" }) {
    const baseClasses = "px-4 py-2 rounded-md transition-all font-medium";
    const variantClasses = variant === "ghost"
        ? "bg-transparent hover:bg-gray-800 border border-gray-700"
        : "bg-blue-600 hover:bg-blue-700";

    return (
        <button
            onClick={onClick}
            className={`${baseClasses} ${variantClasses}`}
        >
            {children}
        </button>
    );
}

export default function App() {
    const [dark, setDark] = useState(true);
    const toggleTheme = () => setDark(!dark);

    const services = [
        "Identité visuelle",
        "Design UX/UI",
        "Création de sites web",
        "Stratégie créative"
    ];

    return (
        <div className={`min-h-screen ${dark ? "bg-black text-white" : "bg-white text-black"}`}>
            {/* Header commun */}
            <header className="flex justify-between items-center p-6">
                <div className="flex items-center gap-3">
                    <div className="w-12 h-12 bg-gradient-to-tr from-teal-700 via-fuchsia-700 to-indigo-700 rounded-md grid grid-cols-2 grid-rows-2">
                        <div className="bg-teal-700 rounded-bl-[100%]" />
                        <div className="bg-fuchsia-700 rounded-br-[100%]" />
                        <div className="bg-indigo-700 rounded-tl-[100%]" />
                        <div className="bg-yellow-600 rounded-tr-[100%]" />
                    </div>
                    <h1 className="text-3xl font-bold tracking-wide">Beyond</h1>
                </div>
                <Button onClick={toggleTheme} variant="ghost">
                    {dark ? "Light" : "Dark"} Mode
                </Button>
            </header>

            {/* Nouvelle section hero */}
            <section className="container mx-auto px-6 py-20 text-center">
                <motion.div
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.8 }}
                >
                    <h1 className="text-5xl md:text-6xl font-bold mb-6">
                        Your Strategic Partner<br />
                        for Digital Solutions
                    </h1>
                    <p className="text-xl md:text-2xl max-w-3xl mx-auto mb-10">
                        Helping startups and brands to craft expressive and engaging solutions for their software needs.
                    </p>
                    <div className="flex flex-col md:flex-row justify-center gap-4 mb-16">
                        <span className="px-4 py-2 bg-gray-800 text-white rounded-full text-sm">
                            Remix Template
                        </span>
                        <span className="px-4 py-2 border border-gray-300 dark:border-gray-700 rounded-full text-sm">
                            Dark Mode
                        </span>
                    </div>
                </motion.div>

                <motion.div
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    transition={{ delay: 0.5, duration: 1 }}
                    className="max-w-4xl mx-auto"
                >
                    <p className="text-gray-500 mb-4">Tools which I use on a daily basis</p>
                    <div className="flex flex-wrap justify-center gap-4">
                        {['React', 'Tailwind', 'Framer Motion', 'Node.js', 'Figma'].map((tool) => (
                            <span key={tool} className="px-3 py-1 bg-gray-100 dark:bg-gray-800 rounded-full text-sm">
                                {tool}
                            </span>
                        ))}
                    </div>
                </motion.div>
            </section>

            {/* Section existante (votre contenu original) */}
            <motion.section
                initial={{ opacity: 0 }}
                whileInView={{ opacity: 1 }}
                transition={{ duration: 1 }}
                className="text-center py-20 px-4"
            >
                <h2 className="text-5xl font-extrabold mb-4">Design beyond the screen</h2>
                {/* ... reste de votre contenu existant ... */}
            </motion.section>

            {/* ... le reste de votre code existant ... */}
        </div>
    );
}
