import { useState, useRef } from 'react';
import { motion, useScroll, useTransform } from 'framer-motion';

// Configuration
const config = {
    logo: {
        light: '/beyond_logo_light.svg',
        dark: '/beyond_logo_dark.svg'
    },
    socials: [
        { name: 'LinkedIn', url: '#', icon: '/icons/linkedin.svg' },
        { name: 'Instagram', url: '#', icon: '/icons/instagram.svg' }
    ],
    services: [
        {
            title: "Identité visuelle",
            description: "Création d'une identité unique pour votre marque",
            illustration: "/illustrations/identity.svg"
        },
        {
            title: "Branding",
            description: "Stratégie de marque complète",
            illustration: "/illustrations/branding.svg"
        },
        {
            title: "UI/UX Design",
            description: "Interfaces intuitives et expériences utilisateur",
            illustration: "/illustrations/uiux.svg"
        },
        {
            title: "Développement web",
            description: "Solutions techniques sur-mesure",
            illustration: "/illustrations/dev.svg"
        }
    ]
};

// Composant ThemeToggle
const ThemeToggle = ({ darkMode, setDarkMode }) => {
    return (
        <button
            onClick={() => setDarkMode(!darkMode)}
            className={`p-2 rounded-full ${darkMode ? 'bg-white/10 hover:bg-white/20' : 'bg-black/10 hover:black/20'} transition-colors`}
            aria-label="Toggle dark mode"
        >
            {darkMode ? (
                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 3v1m0 16v1m9-9h-1M4 12H3m15.364 6.364l-.707-.707M6.343 6.343l-.707-.707m12.728 0l-.707.707M6.343 17.657l-.707.707M16 12a4 4 0 11-8 0 4 4 0 018 0z" />
                </svg>
            ) : (
                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M20.354 15.354A9 9 0 018.646 3.646 9.003 9.003 0 0012 21a9.003 9.003 0 008.354-5.646z" />
                </svg>
            )}
        </button>
    );
};

// Composant SocialLinks
const SocialLinks = ({ vertical, darkMode }) => {
    return (
        <motion.div
            className={`fixed ${vertical ? 'right-6 top-1/2 transform -translate-y-1/2' : 'bottom-6 left-1/2 transform -translate-x-1/2'} z-40 flex ${vertical ? 'flex-col' : 'flex-row'} gap-4`}
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 0.6 }}
        >
            {config.socials.map((social, index) => (
                <motion.a
                    key={index}
                    href={social.url}
                    target="_blank"
                    rel="noopener noreferrer"
                    whileHover={{ y: -3 }}
                    className={`p-3 rounded-full ${darkMode ? 'bg-white/10 hover:bg-white/20' : 'bg-black/10 hover:bg-black/20'} transition-colors`}
                >
                    <img
                        src={social.icon}
                        alt={social.name}
                        className="w-5 h-5"
                    />
                </motion.a>
            ))}
        </motion.div>
    );
};

// Composant ServiceCard
const ServiceCard = ({ service, index, darkMode }) => {
    return (
        <motion.div
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true, margin: "-50px" }}
            transition={{ duration: 0.6, delay: index * 0.1 }}
            className={`p-8 rounded-2xl ${darkMode ? 'bg-[#1a1a1a]' : 'bg-white'} shadow-lg`}
        >
            <div className="flex flex-col md:flex-row gap-8">
                <div className="flex-1">
                    <h3 className="text-2xl font-medium mb-4">{service.title}</h3>
                    <p className={`mb-6 ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>
                        {service.description}
                    </p>
                </div>
                <div className="flex-1 flex items-center justify-center">
                    <img
                        src={service.illustration}
                        alt={service.title}
                        className="max-h-40 object-contain"
                    />
                </div>
            </div>
        </motion.div>
    );
};

// Composant SectionWrapper
const SectionWrapper = ({ id, children, darkMode, type }) => {
    const backgroundStyles = {
        services: darkMode
            ? 'bg-gradient-to-b from-[#0a0a0a] via-[#111111] to-[#0a0a0a]'
            : 'bg-gradient-to-b from-[#f5f5f5] via-[#fefefe] to-[#f5f5f5]',
        projects: darkMode
            ? 'bg-gradient-to-b from-[#111111] via-[#1a1a1a] to-[#111111]'
            : 'bg-gradient-to-b from-[#fefefe] via-[#f9f9f9] to-[#fefefe]'
    };

    return (
        <section
            id={id}
            className={`py-20 ${backgroundStyles[type]}`}
        >
            <div className="container mx-auto px-6">
                {children}
            </div>
        </section>
    );
};

// Composant principal
export default function Home() {
    const [darkMode, setDarkMode] = useState(false);
    const containerRef = useRef(null);

    return (
        <div className={`min-h-screen ${darkMode ? 'bg-[#0a0a0a]' : 'bg-[#f9f9f7]'}`} ref={containerRef}>
            {/* Header */}
            <header className={`fixed w-full z-50 ${darkMode ? 'bg-[#0a0a0a]/90' : 'bg-[#f9f9f7]/90'} backdrop-blur-md`}>
                <div className="container mx-auto px-6 py-4 flex justify-between items-center">
                    <div className="w-12 h-12">
                        <img
                            src={darkMode ? config.logo.light : config.logo.dark}
                            alt="Beyond"
                            className="w-full h-full"
                        />
                    </div>
                    <ThemeToggle darkMode={darkMode} setDarkMode={setDarkMode} />
                </div>
            </header>

            {/* Hero Section */}
            <section className="min-h-screen flex relative">
                <SocialLinks vertical={true} darkMode={darkMode} />

                <div className="container mx-auto px-6 flex items-center justify-center">
                    <div className="text-center max-w-2xl">
                        <motion.h1
                            initial={{ opacity: 0, y: 20 }}
                            animate={{ opacity: 1, y: 0 }}
                            transition={{ duration: 0.8 }}
                            className="text-5xl md:text-7xl font-medium mb-8"
                        >
                            Design d'exception <br />
                            <span className={darkMode ? 'text-gray-300' : 'text-gray-600'}>pour marques ambitieuses</span>
                        </motion.h1>

                        <motion.div
                            initial={{ opacity: 0 }}
                            animate={{ opacity: 1 }}
                            transition={{ delay: 0.4, duration: 0.8 }}
                            className="mt-12"
                        >
                            <motion.a
                                href="#contact"
                                whileHover={{ scale: 1.05 }}
                                whileTap={{ scale: 0.95 }}
                                className={`inline-block px-12 py-4 rounded-full text-lg ${darkMode ? 'bg-white text-black' : 'bg-black text-white'}`}
                            >
                                Devis gratuit
                            </motion.a>
                        </motion.div>
                    </div>
                </div>
            </section>

            {/* Section Services */}
            <SectionWrapper id="services" darkMode={darkMode} type="services">
                <h2 className="text-3xl md:text-5xl font-medium mb-16">Nos expertises</h2>
                <div className="grid md:grid-cols-2 gap-12">
                    {config.services.map((service, index) => (
                        <ServiceCard key={index} service={service} index={index} darkMode={darkMode} />
                    ))}
                </div>
            </SectionWrapper>

            {/* Section Réalisations */}
            <SectionWrapper id="realisations" darkMode={darkMode} type="projects">
                <h2 className="text-3xl md:text-5xl font-medium mb-16">Nos réalisations</h2>
                <div className="grid md:grid-cols-2 gap-8">
                    {/* Contenu des réalisations */}
                </div>
            </SectionWrapper>

            {/* Footer */}
            <footer className={`py-12 ${darkMode ? 'bg-[#0a0a0a]' : 'bg-[#f9f9f7]'} border-t ${darkMode ? 'border-[#1a1a1a]' : 'border-[#eaeaea]'}`}>
                <div className="container mx-auto px-6 text-center">
                    <p className="text-sm">© {new Date().getFullYear()} Beyond. Tous droits réservés.</p>
                </div>
            </footer>
        </div>
    );
}
