import { useState } from "react";
import { motion } from "framer-motion";
import { Button } from "@/components/ui/button";

export default function Beyond() {
    const [dark, setDark] = useState(true);
    const toggleTheme = () => setDark(!dark);

    const services = [
        "Identité visuelle",
        "Design UX/UI",
        "Création de sites web",
        "Stratégie créative"
    ];

    return (
        <div className={dark ? "bg-black text-white" : "bg-white text-black"}>
            <header className="flex justify-between items-center p-6">
                <div className="flex items-center gap-3">
                    <div className="w-12 h-12 bg-gradient-to-tr from-teal-700 via-fuchsia-700 to-olive-700 rounded-md grid grid-cols-2 grid-rows-2">
                        <div className="bg-teal-700 rounded-bl-[100%]" />
                        <div className="bg-fuchsia-700 rounded-br-[100%]" />
                        <div className="bg-indigo-900 rounded-tl-[100%]" />
                        <div className="bg-olive-700 rounded-tr-[100%]" />
                    </div>
                    <h1 className="text-3xl font-bold tracking-wide">Beyond</h1>
                </div>
                <Button onClick={toggleTheme} variant="ghost">
                    {dark ? "Light" : "Dark"} Mode
                </Button>
            </header>

            <motion.section
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 1 }}
                className="text-center py-20"
            >
                <h2 className="text-5xl font-extrabold mb-4">Design beyond the screen</h2>
                <p className="text-xl text-gray-400 max-w-2xl mx-auto">
                    Une agence créative qui repousse les frontières du design numérique pour révéler votre identité.
                </p>
            </motion.section>

            <section className="grid md:grid-cols-2 gap-10 p-10">
                {services.map((s, i) => (
                    <motion.div
                        key={i}
                        initial={{ opacity: 0, y: 10 }}
                        whileInView={{ opacity: 1, y: 0 }}
                        viewport={{ once: true }}
                        transition={{ duration: 0.6, delay: i * 0.2 }}
                        className="p-6 bg-opacity-10 backdrop-blur-md rounded-2xl shadow-lg border border-gray-700"
                    >
                        <h3 className="text-2xl font-semibold mb-2">{s}</h3>
                        <p className="text-sm text-gray-400">Nous transformons vos idées en expériences visuelles puissantes.</p>
                    </motion.div>
                ))}
            </section>

            <footer className="border-t border-gray-700 mt-10 p-6 text-center text-gray-500">
                <p>&copy; 2025 Beyond. Tous droits réservés.</p>
                <p className="mt-2">Contact : contact@beyond.agency</p>
            </footer>
        </div>
    );
}
