    return (
        <footer className={`py-16 ${darkMode ? 'bg-[#0a0a0a]' : 'bg-[#f5f5f5]'} border-t ${darkMode ? 'border-[#1f1f1f]' : 'border-[#eaeaea]'}`}>
            <div className="container mx-auto px-5">
                <div className="grid md:grid-cols-4 gap-8">
                    {/* Colonne Logo */}
                    <div>
                        <div className="w-32 h-22 md:w-40 md:h-30">
                            <img
                                src={darkMode ? config.logoLight : config.logoDark}
                                alt={config.nomAgence}
                                className="w-full h-full"
                            />
                        </div>
                        <p className={`text-sm ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>
                            {config.nomAgence} - {config.slogan}
                        </p>
                    </div>

                    {/* Colonne Contact */}
                    <div>
                        <h4 className="text-sm uppercase tracking-wider mb-6">Contact</h4>
                        <ul className={`space-y-3 text-sm ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>
                            <li>{config.email}</li>
                            <li>{config.telephone}</li>
                            <li>{config.adresse}</li>
                        </ul>
                    </div>
                    {/* Colonne Légal */}
                    <div>
                        <h4 className="text-sm uppercase tracking-wider mb-6">Légal</h4>
                        <ul className={`space-y-3 text-sm ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>
                            <li>
                                <a href="/mentions-legales">Mentions légales</a>
                            </li>
                            <li>
                                <a href="/politique-de-confidentialite">Politique de confidentialité</a>
                            </li>
                            <li>
                                <a href="/cgv">Conditions générales</a>
                            </li>
                        </ul>
                    </div>
                </div>

                {/* Copyright */}
                <div className={`mt-16 pt-8 border-t ${darkMode ? 'border-[#1f1f1f]' : 'border-[#eaeaea]'} text-center text-sm ${darkMode ? 'text-gray-500' : 'text-gray-400'}`}>
                    <p>© {new Date().getFullYear()} {config.nomAgence}. Tous droits réservés.</p>
                </div>
            </div>
        </footer>
    );
}
