import { useState, useRef } from 'react';
import { motion, useScroll, useTransform } from 'framer-motion';

// Configuration
const config = {
    logo: {
        light: '/logo-light.svg',
        dark: '/logo-dark.svg'
    },
    socials: [
        { name: 'LinkedIn', url: '#', icon: '/icons/linkedin.svg' },
        { name: 'Instagram', url: '#', icon: '/icons/instagram.svg' }
    ],
    services: [
        {
            title: "Identité visuelle",
            description: "Création d'une identité unique pour votre marque",
            illustration: "/illustrations/identity.svg"
        },
        // ... autres services
    ]
};

export default function Home() {
    const [darkMode, setDarkMode] = useState(false);
    const containerRef = useRef(null);

    return (
        <div className={`min-h-screen ${darkMode ? 'bg-[#0a0a0a]' : 'bg-[#f9f9f7]'}`} ref={containerRef}>
            {/* Header */}
            <header className={`fixed w-full z-50 ${darkMode ? 'bg-[#0a0a0a]/90' : 'bg-[#f9f9f7]/90'} backdrop-blur-md`}>
                <div className="container mx-auto px-6 py-4 flex justify-between items-center">
                    <div className="w-12 h-12">
                        <img
                            src={darkMode ? config.logo.light : config.logo.dark}
                            alt="Beyond"
                            className="w-full h-full"
                        />
                    </div>
                    <ThemeToggle darkMode={darkMode} setDarkMode={setDarkMode} />
                </div>
            </header>

            {/* Hero Section avec réseaux sociaux */}
            <section className="min-h-screen flex relative">
                {/* Réseaux sociaux verticaux */}
                <SocialLinks vertical={true} darkMode={darkMode} />

                <div className="container mx-auto px-6 flex items-center justify-center">
                    <div className="text-center max-w-2xl">
                        <motion.h1
                            initial={{ opacity: 0, y: 20 }}
                            animate={{ opacity: 1, y: 0 }}
                            transition={{ duration: 0.8 }}
                            className="text-5xl md:text-7xl font-medium mb-8"
                        >
                            Design d'exception <br />
                            <span className={darkMode ? 'text-gray-300' : 'text-gray-600'}>pour marques ambitieuses</span>
                        </motion.h1>

                        <motion.div
                            initial={{ opacity: 0 }}
                            animate={{ opacity: 1 }}
                            transition={{ delay: 0.4, duration: 0.8 }}
                            className="mt-12"
                        >
                            <motion.a
                                href="#contact"
                                whileHover={{ scale: 1.05 }}
                                whileTap={{ scale: 0.95 }}
                                className={`inline-block px-12 py-4 rounded-full text-lg ${darkMode ? 'bg-white text-black' : 'bg-black text-white'}`}
                            >
                                Devis gratuit
                            </motion.a>
                        </motion.div>
                    </div>
                </div>
            </section>

            {/* Section Services avec fond spécial */}
            <SectionWrapper id="services" darkMode={darkMode} type="services">
                <h2 className="text-3xl md:text-5xl font-medium mb-16">Nos expertises</h2>
                <div className="grid md:grid-cols-2 gap-12">
                    {config.services.map((service, index) => (
                        <ServiceCard key={index} service={service} index={index} darkMode={darkMode} />
                    ))}
                </div>
            </SectionWrapper>

            {/* Section Réalisations avec fond spécial */}
            <SectionWrapper id="realisations" darkMode={darkMode} type="projects">
                <h2 className="text-3xl md:text-5xl font-medium mb-16">Nos réalisations</h2>
                <div className="grid md:grid-cols-2 gap-8">
                    {/* Projets ici */}
                </div>
            </SectionWrapper>

            {/* Footer simplifié */}
            <footer className={`py-12 ${darkMode ? 'bg-[#0a0a0a]' : 'bg-[#f9f9f7]'} border-t ${darkMode ? 'border-[#1a1a1a]' : 'border-[#eaeaea]'}`}>
                <div className="container mx-auto px-6 text-center">
                    <p className="text-sm">© {new Date().getFullYear()} Beyond. Tous droits réservés.</p>
                </div>
            </footer>
        </div>
    );
}

// Composant SectionWrapper avec fonds spéciaux
function SectionWrapper({ id, children, darkMode, type }) {
    const backgroundStyles = {
        services: darkMode
            ? 'bg-gradient-to-b from-[#0a0a0a] via-[#111111] to-[#0a0a0a]'
            : 'bg-gradient-to-b from-[#f5f5f5] via-[#fefefe] to-[#f5f5f5]',
        projects: darkMode
            ? 'bg-gradient-to-b from-[#111111] via-[#1a1a1a] to-[#111111]'
            : 'bg-gradient-to-b from-[#fefefe] via-[#f9f9f9] to-[#fefefe]'
    };

    return (
        <section
            id={id}
            className={`py-20 ${backgroundStyles[type]}`}
        >
            <div className="container mx-auto px-6">
                {children}
            </div>
        </section>
    );
}

// Composant SocialLinks
function SocialLinks({ vertical, darkMode }) {
    return (
        <motion.div
            className={`fixed ${vertical ? 'right-6 top-1/2 transform -translate-y-1/2' : 'bottom-6 left-1/2 transform -translate-x-1/2'} z-40 flex ${vertical ? 'flex-col' : 'flex-row'} gap-4`}
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 0.6 }}
        >
            {config.socials.map((social, index) => (
                <motion.a
                    key={index}
                    href={social.url}
                    target="_blank"
                    rel="noopener noreferrer"
                    whileHover={{ y: -3 }}
                    className={`p-3 rounded-full ${darkMode ? 'bg-white/10 hover:bg-white/20' : 'bg-black/10 hover:bg-black/20'} transition-colors`}
                >
                    <img
                        src={social.icon}
                        alt={social.name}
                        className="w-5 h-5"
                    />
                </motion.a>
            ))}
        </motion.div>
    );
}

// Autres composants (ThemeToggle, ServiceCard) restent similaires
