import { useState, useRef } from 'react';
import { motion, useScroll, useTransform } from 'framer-motion';

// Configuration modifiable
const config = {
    // Personalisation
    logoLight: 'beyond_logo_light.svg',
    logoDark: 'beyond_logo_dark.svg',
    nomAgence: "Beyond",
    slogan: "Votre identité, notre vision.",

    // Réseaux sociaux
    reseaux: {
        instagram: "https://instagram.com/votrecompte",
        linkedin: "www.linkedin.com/company/beyond-agencyfr",
    },

    // Contact
    email: "contact@beyond.studio",
    telephone: "+33 1 23 45 67 89",
    adresse: "Poitiers, France",

    // Services illustrés
    services: [
        {
            titre: "Identité visuelle",
            description: "Création d'une identité unique pour votre marque",
            illustration: "/illustrations/id_visu.jpg",
            motsCles: ["Logo", "Charte graphique", "Direction artistique"]
        },
        {
            titre: "Marketing publicitaire",
            description: "Développement de votre publicité pour renforcer l'identité de votre marque",
            illustration: "/illustrations/pub.jpg",
            motsCles: ["Publicité", "Stratégie", "Montage vidéo"]
        },
        {
            titre: "UI/UX Design",
            description: "Interfaces intuitives et expériences mémorables",
            illustration: "/illustrations/uxui.jpg",
            motsCles: ["Prototypage", "Expérience utilisateur", "Application mobile"]
        },
        {
            titre: "Développement web",
            description: "Solutions techniques sur-mesure",
            illustration: "/illustrations/design_web.jpg",
            motsCles: ["React", "Html", "Css", "Javascript"]
        }
    ],

    // Réalisations récentes
    realisations: [
        {
            titre: "Clando Ga",
            description: "Identité visuelle & Application mobile",
            categories: ["Branding", "UI/UX", "Développement"],
            vignette: "/illustrations/clandoga.jpg",
            lien: "/portfolio/nova"
        },
        {
            titre: "Lumen",
            description: "Application mobile & Design system",
            categories: ["UI/UX", "Design System", "Frontend"],
            vignette: "/projets/lumen.jpg",
            lien: "/portfolio/lumen"
        }
    ]
};

export default function Home() {
    const [darkMode, setDarkMode] = useState(false);
    const containerRef = useRef(null);

    const { scrollYProgress } = useScroll({
        target: containerRef,
        offset: ["start start", "end end"]
    });

    // Animations basées sur le scroll
    const opacity = useTransform(scrollYProgress, [0, 0.1], [1, 0]);
    const y = useTransform(scrollYProgress, [0, 0.1], [0, -100]);

    return (
        <div
            className={`relative ${darkMode ? 'bg-[#0a0a0a] text-gray-100' : 'bg-[#faf9f5] text-[#1a1a1a]'}`}
            ref={containerRef}
        >
            {/* Vidéo de fond pour l'héro */}
            <div className="fixed inset-0 z-0 overflow-hidden">
                <video
                    autoPlay
                    loop
                    muted
                    playsInline
                    className="w-full h-full object-cover opacity-20"
                >
                    <source src="/videos/video_background.mp" type="video/mp4" />
                </video>
            </div>

            {/* Header sticky */}
            <motion.header
                style={{ opacity, y }}
                className={`fixed w-full z-50 ${darkMode ? 'bg-[#0a0a0a]/80' : 'bg-[#faf9f5]/80'} backdrop-blur-md border-b ${darkMode ? 'border-[#1f1f1f]' : 'border-[#eaeaea]'}`}
            >
                <div className="container mx-auto px-6 py-4 flex items-center justify-between">
                    <div className="w-32 h-22 md:w-22 md:h-22">
                        <img
                            src={darkMode ? config.logoLight : config.logoDark}
                            alt={config.nomAgence}
                            className="w-full h-full"
                        />
                    </div>

                    <nav className="hidden md:flex items-center gap-8">
                        <a href="#accueil" className="nav-link">Accueil</a>
                        <a href="#services" className="nav-link">Services</a>
                        <a href="#realisations" className="nav-link">Réalisations</a>
                        <a href="/portfolio" className="nav-link">Portfolio</a>
                        <a href="/contact" className="nav-link">Contact</a>
                    </nav>

                    <ThemeToggle darkMode={darkMode} setDarkMode={setDarkMode} />
                </div>
            </motion.header>

            {/* Contenu principal avec ancres */}
            <main className="relative z-10">
                {/* Section Accueil */}
                <section id="accueil" className="min-h-screen flex items-center relative">
                    <div className="container mx-auto px-6 py-32">
                        <motion.div
                            initial={{ opacity: 0, y: 40 }}
                            animate={{ opacity: 1, y: 0 }}
                            transition={{ duration: 1 }}
                            className="max-w-4xl"
                        >
                            <h1 className="text-5xl md:text-7xl font-medium mb-6 leading-tight">
                                {config.nomAgence} —<br />
                                <span className={darkMode ? 'text-gray-300' : 'text-gray-600'}>
                  {config.slogan}
                </span>
                            </h1>
                            <p className="text-xl md:text-2xl mb-10 max-w-2xl">
                                Nous transformons les idées en expériences digitales exceptionnelles.
                            </p>
                            <div className="flex gap-4">
                                <motion.a
                                    href="#services"
                                    whileHover={{ y: -2 }}
                                    whileTap={{ scale: 0.98 }}
                                    className={`px-8 py-3 rounded-full ${darkMode ? 'bg-white text-black' : 'bg-black text-white'}`}
                                >
                                    Nos services
                                </motion.a>
                                <motion.a
                                    href="#realisations"
                                    whileHover={{ y: -2 }}
                                    whileTap={{ scale: 0.98 }}
                                    className={`px-8 py-3 rounded-full border ${darkMode ? 'border-gray-600 hover:bg-gray-800' : 'border-gray-300 hover:bg-gray-100'}`}
                                >
                                    Nos réalisations
                                </motion.a>
                            </div>
                        </motion.div>
                    </div>
                </section>

                {/* Section Services */}
                <section id="services" className="py-20 bg-gradient-to-b from-transparent via-transparent to-transparent">
                    <div className="container mx-auto px-6">
                        <motion.div
                            initial={{ opacity: 0 }}
                            whileInView={{ opacity: 1 }}
                            viewport={{ once: true, margin: "-100px" }}
                            transition={{ duration: 0.8 }}
                        >
                            <h2 className="text-3xl md:text-5xl font-medium mb-16">Nos expertises</h2>

                            <div className="grid md:grid-cols-2 gap-12">
                                {config.services.map((service, index) => (
                                    <ServiceCard
                                        key={index}
                                        service={service}
                                        darkMode={darkMode}
                                        index={index}
                                    />
                                ))}
                            </div>
                        </motion.div>
                    </div>
                </section>

                {/* Section Réalisations */}
                <section id="realisations" className="py-20">
                    <div className="container mx-auto px-6">
                        <motion.div
                            initial={{ opacity: 0 }}
                            whileInView={{ opacity: 1 }}
                            viewport={{ once: true, margin: "-100px" }}
                            transition={{ duration: 0.8 }}
                        >
                            <h2 className="text-3xl md:text-5xl font-medium mb-16">Récentes réalisations</h2>

                            <div className="grid md:grid-cols-2 gap-12">
                                {config.realisations.map((realisation, index) => (
                                    <ProjectCard
                                        key={index}
                                        projet={realisation}
                                        darkMode={darkMode}
                                    />
                                ))}
                            </div>

                            <div className="mt-16 text-center">
                                <motion.a
                                    href="/portfolio"
                                    whileHover={{ y: -2 }}
                                    whileTap={{ scale: 0.98 }}
                                    className={`inline-block px-8 py-3 border ${darkMode ? 'border-gray-600 hover:bg-gray-800' : 'border-gray-300 hover:bg-gray-100'} rounded-full`}
                                >
                                    Voir tout le portfolio
                                </motion.a>
                            </div>
                        </motion.div>
                    </div>
                </section>
            </main>

            {/* Footer */}
            <Footer darkMode={darkMode} config={config} />
        </div>
    );
}

// Composant ServiceCard
function ServiceCard({ service, darkMode, index }) {
    return (
        <motion.div
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true, margin: "-50px" }}
            transition={{ duration: 0.6, delay: index * 0.1 }}
            className={`p-8 rounded-2xl ${darkMode ? 'bg-[#1a1a1a]' : 'bg-white'} shadow-lg`}
        >
            <div className="flex flex-col md:flex-row gap-8">
                <div className="flex-1">
                    <h3 className="text-2xl font-medium mb-4">{service.titre}</h3>
                    <p className={`mb-6 ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>
                        {service.description}
                    </p>
                    <div className="flex flex-wrap gap-2">
                        {service.motsCles.map((mot, i) => (
                            <span
                                key={i}
                                className={`px-3 py-1 rounded-full text-xs ${darkMode ? 'bg-[#0a0a0a] text-gray-300' : 'bg-gray-100 text-gray-800'}`}
                            >
                {mot}
              </span>
                        ))}
                    </div>
                </div>
                <div className="flex-1 flex items-center justify-center">
                    <img
                        src={service.illustration}
                        alt={service.titre}
                        className="max-h-40 object-contain"
                    />
                </div>
            </div>
        </motion.div>
    );
}

// Composant ProjectCard
function ProjectCard({ projet, darkMode }) {
    return (
        <motion.div
            whileHover={{ y: -5 }}
            className="group"
        >
            <div className={`relative overflow-hidden rounded-2xl aspect-video ${darkMode ? 'bg-[#1a1a1a]' : 'bg-gray-200'}`}>
                <img
                    src={projet.vignette}
                    alt={projet.titre}
                    className="w-full h-full object-cover"
                />
                <div className="absolute inset-0 bg-gradient-to-t from-black/80 to-transparent opacity-0 group-hover:opacity-100 transition-opacity flex items-end p-8">
                    <div>
                        <h3 className="text-2xl text-white font-medium">{projet.titre}</h3>
                        <p className="text-gray-300 mt-2">{projet.description}</p>
                        <div className="flex flex-wrap gap-2 mt-4">
                            {projet.categories.map((categorie, i) => (
                                <span
                                    key={i}
                                    className="px-3 py-1 rounded-full bg-white/10 text-white text-xs"
                                >
                  {categorie}
                </span>
                            ))}
                        </div>
                    </div>
                </div>
            </div>
        </motion.div>
    );
}

// Composant ThemeToggle
function ThemeToggle({ darkMode, setDarkMode }) {
    return (
        <button
            onClick={() => setDarkMode(!darkMode)}
            className="p-2 rounded-full hover:bg-opacity-10 hover:bg-gray-500 transition-all"
            aria-label="Toggle dark mode"
        >
            {darkMode ? (
                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 3v1m0 16v1m9-9h-1M4 12H3m15.364 6.364l-.707-.707M6.343 6.343l-.707-.707m12.728 0l-.707.707M6.343 17.657l-.707.707M16 12a4 4 0 11-8 0 4 4 0 018 0z" />
                </svg>
            ) : (
                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M20.354 15.354A9 9 0 018.646 3.646 9.003 9.003 0 0012 21a9.003 9.003 0 008.354-5.646z" />
                </svg>
            )}
        </button>
    );
}

// Composant Footer
function Footer({ darkMode, config }) {
    return (
        <footer className={`py-16 ${darkMode ? 'bg-[#0a0a0a]' : 'bg-[#f5f5f5]'} border-t ${darkMode ? 'border-[#1f1f1f]' : 'border-[#eaeaea]'}`}>
            <div className="container mx-auto px-5">
                <div className="grid md:grid-cols-4 gap-8">
                    {/* Colonne Logo */}
                    <div>
                        <div className="w-32 h-22 md:w-40 md:h-30">
                            <img
                                src={darkMode ? config.logoLight : config.logoDark}
                                alt={config.nomAgence}
                                className="w-full h-full"
                            />
                        </div>
                        <p className={`text-sm ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>
                            {config.nomAgence} - {config.slogan}
                        </p>
                    </div>

                    {/* Colonne Contact */}
                    <div>
                        <h4 className="text-sm uppercase tracking-wider mb-6">Contact</h4>
                        <ul className={`space-y-3 text-sm ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>
                            <li>{config.email}</li>
                            <li>{config.telephone}</li>
                            <li>{config.adresse}</li>
                        </ul>
                    </div>

                    {/* Colonne Réseaux */}
                    <div>
                        <h4 className="text-sm uppercase tracking-wider mb-6">Réseaux</h4>
                        <ul className={`space-y-3 text-sm ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>
                            <li>
                                <a href={config.reseaux.instagram} target="_blank" rel="noopener noreferrer">
                                    Instagram
                                </a>
                            </li>
                            <li>
                                <a href={config.reseaux.linkedin} target="_blank" rel="noopener noreferrer">
                                    LinkedIn
                                </a>
                            </li>
                            <li>
                                <a href={config.reseaux.dribbble} target="_blank" rel="noopener noreferrer">
                                    Dribbble
                                </a>
                            </li>
                        </ul>
                    </div>

                    {/* Colonne Légal */}
                    <div>
                        <h4 className="text-sm uppercase tracking-wider mb-6">Légal</h4>
                        <ul className={`space-y-3 text-sm ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>
                            <li>
                                <a href="/mentions-legales">Mentions légales</a>
                            </li>
                            <li>
                                <a href="/politique-de-confidentialite">Politique de confidentialité</a>
                            </li>
                            <li>
                                <a href="/cgv">Conditions générales</a>
                            </li>
                        </ul>
                    </div>
                </div>

                {/* Copyright */}
                <div className={`mt-16 pt-8 border-t ${darkMode ? 'border-[#1f1f1f]' : 'border-[#eaeaea]'} text-center text-sm ${darkMode ? 'text-gray-500' : 'text-gray-400'}`}>
                    <p>© {new Date().getFullYear()} {config.nomAgence}. Tous droits réservés.</p>
                </div>
            </div>
        </footer>
    );
}
