import { useState, useRef } from 'react';
import { motion, useScroll, useTransform } from 'framer-motion';

export default function App() {
    const [darkMode, setDarkMode] = useState(false);
    const [activeSection, setActiveSection] = useState('accueil');
    const ref = useRef(null);

    const { scrollYProgress } = useScroll({
        target: ref,
        offset: ["start start", "end end"]
    });

    const opacity = useTransform(scrollYProgress, [0, 0.1], [1, 0]);
    const y = useTransform(scrollYProgress, [0, 0.1], [0, -100]);

    return (
        <div className={`min-h-screen ${darkMode ? 'bg-[#0a0a0a] text-gray-100' : 'bg-[#faf9f5] text-[#1a1a1a]'}`} ref={ref}>
            {/* Header futuriste */}
            <motion.header
                style={{ opacity, y }}
                className={`fixed w-full z-50 ${darkMode ? 'bg-[#0a0a0a]/80' : 'bg-[#faf9f5]/80'} backdrop-blur-md border-b ${darkMode ? 'border-[#1f1f1f]' : 'border-[#eaeaea]'}`}
            >
                <div className="container mx-auto px-6 py-4 flex items-center justify-between">
                    <div className="w-12 h-12 cursor-pointer" onClick={() => setActiveSection('accueil')}>
                        <svg viewBox="0 0 1280 720" className="w-full h-full" fill={darkMode ? "white" : "black"}>
                            <path d="M2020 3865 l0 -1335 1318 0 1319 0 6 998 c4 548 7 1149 7 1335 l0 337 -1325 0 -1325 0 0 -1335z m1329 520 c26 -270 44 -336 108 -397 58 -55 124 -75 318 -93 99 -10 186 -21 194 -25 7 -4 -29 -10 -80 -14 -198 -16 -328 -35 -367 -55 -46 -23 -108 -90 -128 -136 -7 -18 -20 -104 -29 -191 -24 -240 -26 -254 -35 -224 -5 14 -15 102 -24 195 -15 150 -20 177 -47 233 -31 63 -81 111 -139 134 -30 12 -196 35 -318 44 -51 3 -95 11 -98 15 -3 5 5 9 18 9 13 0 100 9 193 19 194 21 247 40 308 112 51 59 64 106 82 302 24 245 28 251 44 72z"/>
                        </svg>
                    </div>

                    <nav className="hidden md:flex items-center gap-8">
                        {[
                            { id: 'accueil', label: 'Accueil' },
                            { id: 'realisations', label: 'Récentes réalisations' },
                            { id: 'portfolio', label: 'Réalisations' },
                            { id: 'services', label: 'Services' },
                            { id: 'contact', label: 'Contact' }
                        ].map((item) => (
                            <motion.button
                                key={item.id}
                                onClick={() => setActiveSection(item.id)}
                                whileHover={{ y: -2 }}
                                className={`text-xs uppercase tracking-widest ${activeSection === item.id ? (darkMode ? 'text-white' : 'text-black') : (darkMode ? 'text-gray-400' : 'text-gray-500')}`}
                            >
                                {item.label}
                            </motion.button>
                        ))}
                    </nav>

                    <button
                        onClick={() => setDarkMode(!darkMode)}
                        className="p-2 rounded-full hover:bg-opacity-10 hover:bg-gray-500 transition-all"
                    >
                        {darkMode ? (
                            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 3v1m0 16v1m9-9h-1M4 12H3m15.364 6.364l-.707-.707M6.343 6.343l-.707-.707m12.728 0l-.707.707M6.343 17.657l-.707.707M16 12a4 4 0 11-8 0 4 4 0 018 0z" />
                            </svg>
                        ) : (
                            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M20.354 15.354A9 9 0 018.646 3.646 9.003 9.003 0 0012 21a9.003 9.003 0 008.354-5.646z" />
                            </svg>
                        )}
                    </button>
                </div>
            </motion.header>

            {/* Sections */}
            <div className="pt-32">
                {/* Section Accueil */}
                {activeSection === 'accueil' && (
                    <section className="min-h-screen flex items-center">
                        <div className="container mx-auto px-6">
                            <motion.div
                                initial={{ opacity: 0 }}
                                animate={{ opacity: 1 }}
                                transition={{ duration: 0.8 }}
                                className="max-w-5xl mx-auto"
                            >
                                <h1 className="text-5xl md:text-7xl font-medium mb-8 leading-tight">
                                    Beyond —<br />
                                    <span className={`${darkMode ? 'text-gray-300' : 'text-gray-600'}`}>L'art du design</span><br />
                                    réinventé
                                </h1>
                                <p className="text-xl md:text-2xl max-w-2xl mb-12">
                                    Nous transformons les visions en expériences digitales exceptionnelles.
                                </p>
                                <motion.button
                                    whileHover={{ scale: 1.02 }}
                                    whileTap={{ scale: 0.98 }}
                                    className={`px-8 py-4 ${darkMode ? 'bg-white text-black' : 'bg-black text-white'} rounded-full font-medium`}
                                    onClick={() => setActiveSection('realisations')}
                                >
                                    Découvrir notre travail
                                </motion.button>
                            </motion.div>
                        </div>
                    </section>
                )}

                {/* Section Récentes Réalisations */}
                {activeSection === 'realisations' && (
                    <section className="min-h-screen py-20">
                        <div className="container mx-auto px-6">
                            <h2 className="text-3xl md:text-5xl font-medium mb-16">Récentes réalisations</h2>

                            <div className="grid md:grid-cols-2 gap-12">
                                {/* Projet 1 */}
                                <motion.div
                                    initial={{ opacity: 0, y: 20 }}
                                    animate={{ opacity: 1, y: 0 }}
                                    transition={{ duration: 0.6 }}
                                    className="group"
                                >
                                    <div className={`relative overflow-hidden rounded-xl aspect-video ${darkMode ? 'bg-[#1a1a1a]' : 'bg-gray-200'}`}>
                                        <div className="absolute inset-0 bg-gradient-to-t from-black/80 to-transparent opacity-0 group-hover:opacity-100 transition-opacity flex items-end p-6">
                                            <div>
                                                <h3 className="text-2xl text-white font-medium">Nova Collective</h3>
                                                <p className="text-gray-300 mt-2">Identité visuelle & Site web</p>
                                            </div>
                                        </div>
                                    </div>
                                    <p className="mt-4 text-sm uppercase tracking-wider">Branding • UI/UX • Développement</p>
                                </motion.div>

                                {/* Projet 2 */}
                                <motion.div
                                    initial={{ opacity: 0, y: 20 }}
                                    animate={{ opacity: 1, y: 0 }}
                                    transition={{ duration: 0.6, delay: 0.2 }}
                                    className="group"
                                >
                                    <div className={`relative overflow-hidden rounded-xl aspect-video ${darkMode ? 'bg-[#1a1a1a]' : 'bg-gray-200'}`}>
                                        <div className="absolute inset-0 bg-gradient-to-t from-black/80 to-transparent opacity-0 group-hover:opacity-100 transition-opacity flex items-end p-6">
                                            <div>
                                                <h3 className="text-2xl text-white font-medium">Lumen</h3>
                                                <p className="text-gray-300 mt-2">Application mobile & Design system</p>
                                            </div>
                                        </div>
                                    </div>
                                    <p className="mt-4 text-sm uppercase tracking-wider">UI/UX • Design System • Frontend</p>
                                </motion.div>
                            </div>

                            <div className="mt-16 text-center">
                                <motion.button
                                    whileHover={{ scale: 1.02 }}
                                    whileTap={{ scale: 0.98 }}
                                    className={`px-8 py-3 border ${darkMode ? 'border-gray-700 hover:bg-gray-800' : 'border-gray-300 hover:bg-gray-100'} rounded-full font-medium`}
                                    onClick={() => setActiveSection('portfolio')}
                                >
                                    Voir tout le portfolio
                                </motion.button>
                            </div>
                        </div>
                    </section>
                )}

                {/* Section Portfolio */}
                {activeSection === 'portfolio' && (
                    <section className="min-h-screen py-20">
                        <div className="container mx-auto px-6">
                            <h2 className="text-3xl md:text-5xl font-medium mb-16">Réalisations</h2>

                            <div className="grid md:grid-cols-3 gap-8">
                                {[1, 2, 3, 4, 5, 6].map((item) => (
                                    <motion.div
                                        key={item}
                                        initial={{ opacity: 0, y: 20 }}
                                        animate={{ opacity: 1, y: 0 }}
                                        transition={{ duration: 0.4, delay: item * 0.1 }}
                                        className="group"
                                    >
                                        <div className={`aspect-square rounded-xl overflow-hidden ${darkMode ? 'bg-[#1a1a1a]' : 'bg-gray-200'}`}>
                                            <div className="h-full w-full flex items-center justify-center">
                                                <span className={`text-lg ${darkMode ? 'text-gray-600' : 'text-gray-400'}`}>Projet {item}</span>
                                            </div>
                                        </div>
                                    </motion.div>
                                ))}
                            </div>
                        </div>
                    </section>
                )}

                {/* Section Services */}
                {activeSection === 'services' && (
                    <section className="min-h-screen py-20">
                        <div className="container mx-auto px-6">
                            <h2 className="text-3xl md:text-5xl font-medium mb-16">Services</h2>

                            <div className="grid md:grid-cols-2 gap-12 max-w-5xl mx-auto">
                                {[
                                    {
                                        title: "Identité visuelle",
                                        description: "Création d'identités visuelles distinctives qui captivent et communiquent l'essence de votre marque."
                                    },
                                    {
                                        title: "Branding",
                                        description: "Développement de stratégies de marque complètes avec guidelines et applications."
                                    },
                                    {
                                        title: "UI/UX Design",
                                        description: "Conception d'interfaces intuitives et d'expériences utilisateur mémorables."
                                    },
                                    {
                                        title: "Développement web",
                                        description: "Implémentation technique sur-mesure pour des performances optimales."
                                    }
                                ].map((service, index) => (
                                    <motion.div
                                        key={index}
                                        initial={{ opacity: 0, y: 20 }}
                                        animate={{ opacity: 1, y: 0 }}
                                        transition={{ duration: 0.4, delay: index * 0.1 }}
                                        className={`p-8 rounded-xl ${darkMode ? 'bg-[#1a1a1a]' : 'bg-white shadow-sm'}`}
                                    >
                                        <h3 className="text-2xl font-medium mb-4">{service.title}</h3>
                                        <p className={`${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>{service.description}</p>
                                    </motion.div>
                                ))}
                            </div>
                        </div>
                    </section>
                )}

                {/* Section Contact */}
                {activeSection === 'contact' && (
                    <section className="min-h-screen py-20">
                        <div className="container mx-auto px-6 max-w-4xl">
                            <h2 className="text-3xl md:text-5xl font-medium mb-16">Contact</h2>

                            <motion.form
                                initial={{ opacity: 0 }}
                                animate={{ opacity: 1 }}
                                transition={{ duration: 0.6 }}
                                className={`p-8 rounded-xl ${darkMode ? 'bg-[#1a1a1a]' : 'bg-white shadow-sm'}`}
                            >
                                <div className="grid md:grid-cols-2 gap-6">
                                    <div>
                                        <label className={`block mb-2 text-sm ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>Nom</label>
                                        <input
                                            type="text"
                                            className={`w-full px-4 py-3 rounded-lg ${darkMode ? 'bg-[#0a0a0a] border-gray-800' : 'bg-gray-50 border-gray-200'} border focus:outline-none focus:ring-2 ${darkMode ? 'focus:ring-white' : 'focus:ring-black'}`}
                                        />
                                    </div>
                                    <div>
                                        <label className={`block mb-2 text-sm ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>Email</label>
                                        <input
                                            type="email"
                                            className={`w-full px-4 py-3 rounded-lg ${darkMode ? 'bg-[#0a0a0a] border-gray-800' : 'bg-gray-50 border-gray-200'} border focus:outline-none focus:ring-2 ${darkMode ? 'focus:ring-white' : 'focus:ring-black'}`}
                                        />
                                    </div>
                                </div>

                                <div className="mt-6">
                                    <label className={`block mb-2 text-sm ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>Message</label>
                                    <textarea
                                        rows="5"
                                        className={`w-full px-4 py-3 rounded-lg ${darkMode ? 'bg-[#0a0a0a] border-gray-800' : 'bg-gray-50 border-gray-200'} border focus:outline-none focus:ring-2 ${darkMode ? 'focus:ring-white' : 'focus:ring-black'}`}
                                    ></textarea>
                                </div>

                                <motion.button
                                    whileHover={{ scale: 1.02 }}
                                    whileTap={{ scale: 0.98 }}
                                    className={`mt-8 px-8 py-4 ${darkMode ? 'bg-white text-black' : 'bg-black text-white'} rounded-full font-medium w-full md:w-auto`}
                                >
                                    Envoyer le message
                                </motion.button>
                            </motion.form>
                        </div>
                    </section>
                )}
            </div>

            {/* Footer élégant */}
            <footer className={`py-16 ${darkMode ? 'bg-[#0a0a0a] border-t border-[#1f1f1f]' : 'bg-[#f5f5f5] border-t border-[#eaeaea]'}`}>
                <div className="container mx-auto px-6">
                    <div className="grid md:grid-cols-4 gap-12">
                        <div>
                            <div className="w-12 h-12 mb-6">
                                <svg viewBox="0 0 1280 720" className="w-full h-full" fill={darkMode ? "white" : "black"}>
                                    <path d="M2020 3865 l0 -1335 1318 0 1319 0 6 998 c4 548 7 1149 7 1335 l0 337 -1325 0 -1325 0 0 -1335z m1329 520 c26 -270 44 -336 108 -397 58 -55 124 -75 318 -93 99 -10 186 -21 194 -25 7 -4 -29 -10 -80 -14 -198 -16 -328 -35 -367 -55 -46 -23 -108 -90 -128 -136 -7 -18 -20 -104 -29 -191 -24 -240 -26 -254 -35 -224 -5 14 -15 102 -24 195 -15 150 -20 177 -47 233 -31 63 -81 111 -139 134 -30 12 -196 35 -318 44 -51 3 -95 11 -98 15 -3 5 5 9 18 9 13 0 100 9 193 19 194 21 247 40 308 112 51 59 64 106 82 302 24 245 28 251 44 72z"/>
                                </svg>
                            </div>
                            <p className={`text-sm ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>
                                Beyond - Agence de design digital
                            </p>
                        </div>

                        <div>
                            <h4 className="text-sm uppercase tracking-wider mb-6">Contact</h4>
                            <ul className={`space-y-3 text-sm ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>
                                <li>hello@beyond.studio</li>
                                <li>+33 1 23 45 67 89</li>
                                <li>Paris, France</li>
                            </ul>
                        </div>

                        <div>
                            <h4 className="text-sm uppercase tracking-wider mb-6">Réseaux</h4>
                            <ul className={`space-y-3 text-sm ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>
                                <li>Instagram</li>
                                <li>LinkedIn</li>
                                <li>Dribbble</li>
                            </ul>
                        </div>

                        <div>
                            <h4 className="text-sm uppercase tracking-wider mb-6">Légal</h4>
                            <ul className={`space-y-3 text-sm ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>
                                <li>Mentions légales</li>
                                <li>Politique de confidentialité</li>
                                <li>CGV</li>
                            </ul>
                        </div>
                    </div>

                    <div className={`mt-16 pt-8 border-t ${darkMode ? 'border-[#1f1f1f]' : 'border-[#eaeaea]'} text-center text-sm ${darkMode ? 'text-gray-500' : 'text-gray-400'}`}>
                        <p>© {new Date().getFullYear()} Beyond. Tous droits réservés.</p>
                    </div>
                </div>
            </footer>
        </div>
    );
}
