import { useState } from "react";
import { motion } from "framer-motion";
import { Moon, Sun } from "react-feather"; // Icônes pour le dark mode

function NavLink({ children }) {
    return (
        <a href="#" className="px-4 py-2 hover:text-teal-400 transition-colors">
            {children}
        </a>
    );
}

export default function App() {
    const [dark, setDark] = useState(true);
    const toggleTheme = () => setDark(!dark);

    const navItems = ["Landings", "Work", "Process", "Experience", "FAQ"];

    return (
        <div className={`min-h-screen ${dark ? "bg-black text-white" : "bg-white text-black"}`}>
            {/* Barre de navigation */}
            <header className="flex justify-between items-center p-6 border-b border-gray-800">
                <div className="flex items-center gap-10">
                    {/* Logo */}
                    <div className="flex items-center gap-3">
                        <div className="w-10 h-10 bg-gradient-to-tr from-teal-700 via-fuchsia-700 to-indigo-700 rounded-md grid grid-cols-2 grid-rows-2">
                            <div className="bg-teal-700 rounded-bl-[100%]" />
                            <div className="bg-fuchsia-700 rounded-br-[100%]" />
                            <div className="bg-indigo-700 rounded-tl-[100%]" />
                            <div className="bg-yellow-600 rounded-tr-[100%]" />
                        </div>
                        <h1 className="text-xl font-bold tracking-wide">Beyond</h1>
                    </div>

                    {/* Menu horizontal */}
                    <nav className="hidden md:flex items-center gap-1">
                        {navItems.map((item) => (
                            <NavLink key={item}>{item}</NavLink>
                        ))}
                    </nav>
                </div>

                {/* Bouton Dark Mode avec icône */}
                <button
                    onClick={toggleTheme}
                    className="p-2 rounded-full hover:bg-gray-800 transition-colors"
                    aria-label="Toggle dark mode"
                >
                    {dark ? <Sun size={20} /> : <Moon size={20} />}
                </button>
            </header>

            {/* Votre contenu existant... */}
            <section className="container mx-auto px-6 py-20">
                <h1 className="text-5xl font-bold mb-6">Your Strategic Partner for Digital Solutions</h1>
                {/* ... reste du contenu ... */}
            </section>
        </div>
    );
}
