import { useState } from "react";
import { motion } from "framer-motion";

// Composant Button correctement implémenté
function Button({ onClick, children, variant = "ghost" }) {
    const baseClasses = "px-4 py-2 rounded-md transition-all font-medium";
    const variantClasses = variant === "ghost"
        ? "bg-transparent hover:bg-gray-800 border border-gray-700"
        : "bg-blue-600 hover:bg-blue-700";

    return (
        <button
            onClick={onClick}
            className={`${baseClasses} ${variantClasses}`}
        >
            {children}
        </button>
    );
}

export default function App() {
    const [dark, setDark] = useState(true);
    const toggleTheme = () => setDark(!dark);

    const services = [
        "Identité visuelle",
        "Design UX/UI",
        "Création de sites web",
        "Stratégie créative"
    ];

    return (
        <div className={`min-h-screen ${dark ? "bg-black text-white" : "bg-white text-black"}`}>
            <header className="flex justify-between items-center p-6">
                <div className="flex items-center gap-3">
                    <div className="w-12 h-12 bg-gradient-to-tr from-teal-700 via-fuchsia-700 to-indigo-700 rounded-md grid grid-cols-2 grid-rows-2">
                        <div className="bg-teal-700 rounded-bl-[100%]" />
                        <div className="bg-fuchsia-700 rounded-br-[100%]" />
                        <div className="bg-indigo-700 rounded-tl-[100%]" />
                        <div className="bg-yellow-600 rounded-tr-[100%]" />
                    </div>
                    <h1 className="text-3xl font-bold tracking-wide">Beyond</h1>
                </div>
                <Button onClick={toggleTheme} variant="ghost">
                    {dark ? "Light" : "Dark"} Mode
                </Button>
            </header>

            <motion.section
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 1 }}
                className="text-center py-20 px-4"
            >
                <h2 className="text-5xl font-extrabold mb-4">Design beyond the screen</h2>
                <p className="text-xl text-gray-400 max-w-2xl mx-auto">
                    Une agence créative qui repousse les frontières du design numérique pour révéler votre identité.
                </p>
            </motion.section>

            <section className="grid md:grid-cols-2 gap-10 p-10 max-w-6xl mx-auto">
                {services.map((service, index) => (
                    <motion.div
                        key={index}
                        initial={{ opacity: 0, y: 10 }}
                        whileInView={{ opacity: 1, y: 0 }}
                        viewport={{ once: true }}
                        transition={{ duration: 0.6, delay: index * 0.2 }}
                        className={`p-6 backdrop-blur-md rounded-2xl shadow-lg border ${
                            dark ? "border-gray-700" : "border-gray-200"
                        }`}
                    >
                        <h3 className="text-2xl font-semibold mb-2">{service}</h3>
                        <p className={`text-sm ${dark ? "text-gray-400" : "text-gray-600"}`}>
                            Nous transformons vos idées en expériences visuelles puissantes.
                        </p>
                    </motion.div>
                ))}
            </section>

            <footer className={`border-t ${dark ? "border-gray-700" : "border-gray-200"} mt-10 p-6 text-center`}>
                <p className={dark ? "text-gray-500" : "text-gray-600"}>&copy; 2025 Beyond. Tous droits réservés.</p>
                <p className={`mt-2 ${dark ? "text-gray-500" : "text-gray-600"}`}>
                    Contact : contact@beyond.agency
                </p>
            </footer>
        </div>
    );
}
